/* eslint-disable react/prop-types */
// react 
import React, { useEffect } from 'react';
import { NavLink } from 'react-router-dom';
// redux 
import { useSelector, useDispatch } from 'react-redux';
import { createOrder } from '../../../redux/actions/orderActions'
import * as actionTypes from '../../../redux/constants/orderConstants'
// component
import CheckoutSteps from '../../CheckoutSteps';
import LdgBox from '../../LdgBox';
import MsgBox from '../../MsgBox';
// style 
//import moonPendant from '../../assets/fonts/moon-pendant.png';
import './style.scss';

const PlaceOrder = (props) => {
  const cart = useSelector(state => state.cart);
if (!cart.paymentMethod) {
  props.history.push('/paiement')
}
  // pour recupÃ©rer le state dans le state dans le store :
  const orderCreate = useSelector(state => state.orderCreate);
  const { loading, success, error, order } = orderCreate;

   const getCartCount = () => {
    return cart.cartItems.reduce((qty, item) => Number(item.qty) + qty, 0);
  } // arr.reduce(callback, valeurInitiale)

 /* const getCartSubTotal = () => {
    return cart.cartItems.reduce((price, item) => (item.price * item.qty) + price + cart.shippingPrice, 0)
  } */
  const toPrice = (num) => Number(num.toFixed(2));
  cart.itemsPrice = toPrice(
    cart.cartItems.reduce((a, c) => a + c.qty * c.price, 0)
  );
  cart.shippingPrice = cart.itemsPrice > 100 ? toPrice(0) : toPrice(10);
  cart.taxPrice = toPrice(0.15 * cart.itemsPrice);
  cart.totalPrice = cart.itemsPrice + cart.shippingPrice + cart.taxPrice;
  const dispatch = useDispatch();
  const placeOrderHandler = () => {
    //TODO: dispatch place order action
    dispatch(createOrder({ ...cart, orderItems: cart.cartItems }))
  };
  useEffect(() => {
    if (success) {
      props.history.push(`/commande/${order._id}`);
      dispatch({ type: actionTypes.ORDER_CREATE_RESET });
    }
  }, [dispatch, order, props.history, success])
  return (
    <div className="placeOrder">
      < CheckoutSteps step1 step2 step3 step4></CheckoutSteps>

    <div className="placeOrder__row top">
      <div className="placeOrder__col-2">
        <ul>
          <li className="placeOrder__card__list ">
            <div className="placeOrder__card__expedition card-body">
              <h2 className="placeOrder__title"> ExpÃ©dition</h2>
              <p>
                <strong> Nom :</strong> {cart.shippingAddress.fullName} <br/>
                  <strong> Adresse: </strong> {cart.shippingAddress.address},
                  {cart.shippingAddress.city}, {cart.shippingAddress.postalCode},
                  {cart.shippingAddress.country}

              </p>
            </div>
            </li>
          <li className="placeOrder__card__list ">
            <div className="placeOrder__card__method card-body">
              <h2 className="placeOrder__title"> Paiement </h2>
              <p>
                <strong> MÃ©thode :</strong> {cart.paymentMethod}             
              </p>
            </div>
            </li>
            <li className="placeOrder__card__list ">
            <div className="placeOrder__card__orderedCmd card-body">
                <h2 className="placeOrder__title"> Produits commandÃ©s </h2>
                  {cart.cartItems.map(item => (
                   <li key={item.product}>
                      <div className="placeOrder__card__orderedCmd__row">
                        <div className="placeOrder__card__img">
                          <img
                            src={item.image}
                            alt={item.name}
                            className="small"
                          />
                        {/* <img src={moonPendant} alt="Pendentif Lunaire" /> */}
                        </div>
                        <div className="min-30">
                          <NavLink  className="placeOrder__card__orderedCmd__link" to={`/product/${item.product}`}>
                            {item.name}
                          </NavLink>
                        </div>

                        <div>
                          {item.qty} x â¬{item.price} = â¬{item.qty * item.price}
                        </div>
                      </div>
                    </li>
                  ))
                }
            </div>
          </li>
        </ul>
      </div>
       <div className="placeOrder__col-1">
          <div className="cartScreen__right">
            <ul className="placeOrder__col-1__summary">
              <li>
                <h2 className="placeOrder__col-1__summary__title" > RÃ©sumÃ© de la commande </h2>
              </li>
              <li>
                <div className="cartScreen__right__info">
                    <p> Sous-total : ({getCartCount()}) produit(s) </p> 
                  <p> Frais : d&rsquo;expÃ©dition {cart.shippingPrice.toFixed(2)} â¬</p>
 {/*                  <p> Taxes : {cart.taxPrice.toFixed(2)} â¬</p> */}
                  {/*  // <p> {getCartSubTotal().toFixed(2)} euros </p> */}
                  <p> ${cart.totalPrice.toFixed(2)} </p>
                </div>
              </li>
              <li>
                <button
                  type="button "
                  onClick={placeOrderHandler}
                  className="primary block placeOrder__col-1__button"
                  disabled={cart.cartItems.length === 0}
                >
                  Passer la commande 
                </button>
              </li>
               { loading && <LdgBox> </LdgBox> } 
              { error && <MsgBox variant="danger">{error}</MsgBox> }
            </ul>
       </div>
      </div>
      </div>
    </div>
  )
}
export default PlaceOrder;
